Action: TerminalExecute
Action Input: {"command": "echo 'Trade History for BTCUSD in the last month:' > binance_trade_history.csv && echo '' >> binance_trade_history.csv && for i in $(binance-get-trade-history --date-range 2022-01-22 2022-02-22 --pair BTCUSD | jq -r '.[] | @base64'); do echo; echo $i | base64 --decode | jq -r.; done >> binance_trade_history.csv"}
